impl EntityType {
    /// Given a version str and a property_id, find the property name
    pub fn find_property(&self, version: &str, prop_id: usize) -> Result<Option<&'static str>, String> {
        let find_in = |map: &phf::Map<&'static str, &'static [&'static str]>| {
            map.get(version)
                .and_then(|&arr| arr.get(prop_id))
                .map(|prop| *prop)
        };

        let not_found_error = || format!("No prop with id: {prop_id} found for {self} in {version}");

        match self {
            Self::Avatar => find_in(&AVATAR_PROPS).ok_or_else(not_found_error).map(Some),
            Self::Vehicle => find_in(&VEHICLE_PROPS).ok_or_else(not_found_error).map(Some),
            _ => Ok(None),
        }
    }
}

